mapper.insert:
INSERT INTO BOARD
(
    NO
    ,TITLE
    ,CONTENT
    ,WRITER_NO
    ,CATEGORY_NO
)
VALUES
(
    SEQ_BOARD_NO.NEXTVAL
    , #{title}
    , #{content}
    , #{writerNo}
    , #{categoryNo}
)

mapper.update:
	UPDATE BOARD
    SET 
        TITLE = #{title}
        , CONTENT = #{content}
        , MODIFY_DATE = SYSDATE
    WHERE   NO = #{no}
    AND     STATUS = 'O'
    AND     WRITER_NO = #{writerNo}

	UPDATE BOARD
    SET
        STATUS = 'X'
        , MODIFY_DATE = SYSDATE
    WHERE   NO = #{no}
    AND     WRITER_NO = #{writerNo}
