mapper.insert:
  	INSERT INTO MEMBER
  	(
  		NO
		,ID
		,PWD
		,NICK
		,PROFILE
		,HOBBY
  	)
  	VALUES
  	(
  		SEQ_MEMBER_NO.NEXTVAL
		, #{id}
		, #{pwd}
		, #{nick}
		, #{profile}
		, #{hobbyStr}
  	)

mapper.select:

SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE MEMBER
	SET 
		PWD = #{pwd}
		, NICK = #{nick}
		, PROFILE = #{profile}
		, HOBBY = #{hobbyStr}
		, MODIFY_DATE = SYSDATE
	WHERE
		NO = #{no}
