mapper.insert:
  	INSERT INTO MEMBER
  	(
  		NO
		,ID
		,PWD
		,NICK
		,PROFILE
		,HOBBY
  	)
  	VALUES
  	(
  		SEQ_MEMBER_NO.NEXTVAL
		, #{id}
		, #{pwd}
		, #{nick}
		, #{profile}
		, #{hobbyStr}
  	)

mapper.select:

SELECT COUNT(*)
FROM MEMBER
WHERE ID = #{id}

SELECT *
FROM MEMBER
WHERE 	ID = #{id}
AND		PWD = #{pwd}
AND 	STATUS = 'O'
